from typing import List
from collections import deque

class Solution:
    def exist(self, board: List[List[str]], word: str) -> bool:

        maxr = len(board)
        if maxr == 0:
            return False
        maxc = len(board[0])
        if len(word) == 0:
            return False

        def search(s, r, c):

            q = deque([( 0, r, c, s, {(r,c),} )])
            dirs = [ ( 1,  0), (-1,  0), ( 0,  1), ( 0, -1) ]

            while q:
                i, r, c, s, seen = q.pop()
                if s == word:
                    return True

                for dr, dc in dirs:
                    if i >= len(word):
                        return False

                    nr = r + dr
                    nc = c + dc
                    if not (0 <= nr < maxr) or not (0 <= nc < maxc) or (nr, nc) in seen:
                        continue

                    if word[i+1] != board[nr][nc]:
                        continue

                    ns = s + board[nr][nc]
                    nseen = seen.copy()

                    nseen.add((nr, nc))
                    q.append((i+1, nr, nc, ns, nseen))
            return False

        for r, row in enumerate(board):
            for c, cell in enumerate(row):
                if cell == word[0]:
                    if search(word[0], r, c):
                        return True
        return False




